mapper.select:
select COUNT(*)
from FUNDING_PROJECT
where STATUS='Y'

	AND CATEGORY_NO = #{categoryNo}


	AND ${period}


	AND FP_NAME LIKE '%'||#{search}||'%'

select FP_NO, FP_NAME, B.USER_NAME, GOAL,RAISED, START_DATE, CLOSING_DATE , A.CATEGORY_NO, HITS, THUMBNAIL_ORIGIN_NAME , THUMBNAIL_CHANGE_NAME, C.FUNDING_CATEGORY_NAME,CLOSING_DATE-START_DATE AS "D_Day" from FUNDING_PROJECT A join MEMBER B on A.HOST_ID=B.USER_ID join FUNDING_CATEGORY C on A.CATEGORY_NO = C.FUNDING_CATEGORY_NO where A.STATUS='Y'
AND A.CATEGORY_NO = #{categoryNo}
AND ${period}
AND FP_NAME LIKE '%'||#{search}||'%'
ORDER BY ${order}

SELECT FP_NO, IMG_NO, IMG_ORIGIN_NAME, IMG_CHANGE_NAME
FROM FUNDING_IMAGE
WHERE FP_NO=#{fpNo}

SELECT FP_NO ,FG_NO,FG_NAME,FG_CONTENT,FG_PRICE
FROM FUNDING_GOODS
WHERE FP_NO=#{fpNo}

mapper.update:
update FUNDING_REPLY
set STATUS='N'
where REPLY_NO=#{replyNo}

update FUNDING_REPLY
set REPLY_CONTENT=#{replyContent}
where REPLY_NO=#{replyNo}

update FUNDING_PROJECT
set HITS=HITS+1
where FP_NO=#{fpNo}
