mapper.update:
UPDATE GOODS 
SET HITS=HITS+1
WHERE GOODS_NO=#{goodsNo}

mapper.delete:
UPDATE GOODS 
SET STATUS='N'
WHERE GOODS_NO=#{goodsNo}

mapper.update:
UPDATE GOODS 
SET GOODS_NAME=#{goodsName}
,GOODS_CATEGORY_NO=#{goodsCategoryNo}
,CONTENT=#{content}

	,THUMBNAIL_ORIGIN_NAME=#{thumbnailOriginName}
	,THUMBNAIL_CHANGE_NAME=#{thumbnailChangeName}

WHERE GOODS_NO=#{goodsNo}
