mapper.update:
update member
set user_Nick=#{userNick} ,address=#{address}, email=#{email}, phone=#{phone}, birthdate=#{birthdate}, user_role=#{userRole}, modify_date=sysdate
where user_id=#{userId}

mapper.delete:
update member
set status='N'
where user_id=#{userId}

mapper.update:
update member
set user_pwd=#{userPwd}
where user_id=#{userId}
