mapper.update:
UPDATE NOTICE
SET NO_SAVE='Y'
WHERE NO_NO=#{noticeNo}

mapper.select:
SELECT COUNT(*) FROM FAQ JOIN FAQ_TYPE ON F_TYPENUM=F_TYPENO
WHERE F_TYPENUM = #{fTypeNo}
AND F_Q LIKE '%' || #{fTypeName} || '%'

mapper.update:
UPDATE FAQ
SET F_Q=#{faqQuestion},
	F_A=#{faqAnswered}
WHERE F_NO = #{faqNo}

mapper.select:
SELECT COUNT(*) FROM ASK JOIN MEMBER ON ASK_ID=USER_ID JOIN FAQ_TYPE ON ASK_TYPENUM=F_TYPENO
WHERE 1=1
WHERE USER_ID=#{userId}

SELECT ROWNUM, ASK_NO, F_TYPENAME, ASK_TITLE, USER_NAME, ASK_DATE, ASK_STATUS, ASK_CONTENT,ANSWERED FROM ASK JOIN MEMBER ON ASK_ID=USER_ID JOIN FAQ_TYPE ON ASK_TYPENUM=F_TYPENO
WHERE 1=1
WHERE USER_ID=#{userId}
